import logo from './logo.svg';
import {useState,useRef} from 'react';
import './App.css';

import Pencils from './pencils.png';

const initialTodo = [
  'Insira suas tarefas aqui',
  'Comece apagando essas tarefas'
]


//---this function just to make it easier to code----
function log(arg1='---', arg2='')
{
  console.log(arg1,arg2);
}

function App() {

  const [newTask,setNewTask] = useState('');
  const [todo,setTodo] = useState(initialTodo);
  const todoList = useRef(initialTodo);

  const [pLapis, setPlapis] = useState(0);


  //esse state é usado somente para forçar um re-render
  const [render, setRender] = useState(false);


  const movePencil = ()=>{

    if (!pLapis) {
      setPlapis(5);
      return {left: pLapis};

    }
  }

  const addTask = ()=>{

    if (!newTask) return;   
    todoList.current.push(newTask);
    setNewTask('');

    //abaixo: somente para causar um re-render
    setRender(Math.random());
  }


  const deleteTodo = (index)=>{
    log('index',index);

    todoList.current.splice(index,1);
    log('todo',todoList.current);

    //abaixo: somente para causar um re-render
    setRender(Math.random());
  }

  //--------------------------------------------+++++++++++++++++++++++
  log(todo.length);
  if(todo.length) log('tem coisa')  
  else log('[vazio]');
  return (
    <>

    {/*

    Formulário  - Nova Tarefa
    ============================*/}
  <div className="todo_container">
    <h1>Lembrar de fazer</h1>


    <div className='input_box'>
        <div className='wrap_input'>
        <label>
        Nova tarefa:
          <input type="text"
          size='35'
          value={newTask}
          onChange={(e) => {
            if(e.target.value.length < 35)
            setNewTask(e.target.value);
          }}
          ></input>
        </label>

          <div className='wrap_div_btn'>
            <div className="div_btn add_btn" onClick={addTask}> +</div>
          </div>
        </div>

        <div className='wrap_logo'> 
          <img src={Pencils} alt="lapis" className='logo' style={movePencil()}/>
        </div>
      </div>
      {/**
      <div className='wrap_div_btn'>
        <div className="div_btn"> +</div>
      </div>
       */}

    <hr/>

{/*

Lista - toDos
============================*/}
    <div className="tarefas_wrap light_shadow">
      <div className='wrap_list'>
          {(!todo.length) && <h2>Sem tarefas Hoje</h2>}
        { todo.map((el,index)=>{

          return (
          <div className="item_tarefa light_shadow" key={index}>
            <div>{index+1} - {el}</div>
            <div className="delete_btn" onClick={ ()=>{deleteTodo(index)} }>X</div>

            </div>
          );
        })}

    </div>
    { 
    ///feature to be implemented in the future
    //todo.length && <div className='delete_list'>Apagar<br/>Lista</div>

    }

    </div> 

    </div>
    </>
  );
}


{/*===========================*/}
export default App;
