import logo from './logo.svg';
import {useState ,useRef}  from 'react';
import './App.css';

const initialTodo = [
  'Insira suas tarefas aqui',
  'Comece apagando essas tarefas'
]


//---function just to make it easier to code----
function log(arg1='---', arg2='')
{
  console.log(arg1,arg2);
}

function App() {

  const [newTask,setNewTask] = useState('');
  const [todo] = useRef(initialTodo);


  const addTask = (task)=>{
    let newTodo = todo;
    newTodo.push(task)
    log('todo',todo);
    log('new',newTodo);

   // log('final  todo',todo);
  }

  //--------------------------------------------
  const handleSubmit = (event)=>{
    event.preventDefault();

    setNewTask('');
    //return true;
  }

  //addTask('new task');

  console.log('---',todo);


  {//------------------+++++++
  }
  return (
    <>

    {/*

    Formul√°rio  - Nova Tarefa
    ============================*/}
  <div className="todo_container">
    <h1>Lembrar de fazer</h1>

    <form onSubmit={addTask}>

      <label>
      Nova tarefa:
        <input type="text"
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
        ></input>
      </label>

      <button type="submit" >+</button>

    </form>
    <hr/>

{/*

Lista - toDos
============================*/}
    <div className="tarefas_wrap">

        { todo.map((el,index)=>{
          return (
          <div className="item_tarefa" key="index">
            <div>{index} - {el}</div>
            <div className="delete_btn">X</div>
            </div>
          );
        })}

    </div>


    </div>
    </>
  );
}


{/*===========================*/}
export default App;
