import logo from './logo.svg';
import {useState} from 'react';
import './App.css';

const initialTodo = [
  'Insira suas tarefas aqui',
  'Comece apagando essas tarefas'
]


function App() {

  const [newTask,setNewTask] = useState('');
  const [todo,setTodo] = useState(initialTodo);


  const handleSubmit = (event)=>{
    event.preventDefault();

    setNewTask('');
    return true;
  }

  console.log(todo);
  return (
    <>
  <div class="todo_container">
    <h1>Lembrar de fazer</h1>

    <form onSubmit={handleSubmit}>
      <label>
      Nova tarefa:
      <input type="text"
      value={newTask}
      onChange={(e) => setNewTask(e.target.value)}
      ></input>
      </label>
      <button type="submit" class='plus_btn'>+</button>

    </form>
    <hr/>

    { todo.map((el,index)=>{

      return (`

        <div class="tarefas_wrap">
      <div class="item_tarefa">
        <div>${el}</div>
        <div class="delete_btn">X</div>`
      );



    })}

    <div class="tarefas_wrap">
      <div class="item_tarefa">
        <div>Insira suas tarefas aqui</div>
        <div class="delete_btn">X</div>

      </div>
      <div class="item_tarefa">
        <div>Comece apagando essas tarefas</div>
        <div class="delete_btn">X</div>
      </div>
    </div>


    </div>
    </>
  );
}

export default App;
